mapper.select:
SELECT U_ID, D_ID, U_EMAIL, U_PWD, U_NAME, U_POSITION, U_PHONE, U_STATUS
FROM USER_INFO
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, R_STATUS, R_NEWDATE
FROM RESUME
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, A_HSCHOOL, A_UNIVERSITY, A_GSCHOOL, A_HSCHOOLDATE, A_UNIVERSITYDATE, A_GSCHOOLDATE
FROM ACADEMIC
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, CE_CODE, CE_TEXT
FROM CERTIFICATE
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, C_NAME, C_START, C_END, C_POSITION, C_TASK
FROM CAREER
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, E_PLACE, E_STARTED, E_END, E_NAME
FROM EDUCATION
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, ST_AP, ST_LEVEL
FROM SPECIAL_TECH
WHERE U_ID = #{u_id}

SELECT R_ID, U_ID, D_ID, SI_NAME, SI_START, SI_END, SI_CUSTOMER, SI_COMPANY, SI_ROLE, SI_MODEL, SI_OS, SI_LANGUAGE, SI_TOOL, SI_ETC
FROM SKILL_INVENTORY
WHERE U_ID = #{u_id}

mapper.update:
UPDATE RESUME
SET R_STATUS = #{r_id},
R_NEWDATE = #{r_newdate}
WHERE R_ID = #{r_id}

UPDATE CERTIFICATE
SET CE_CODE = #{ce_code},
CE_TEXT = #{ce_text}
WHERE R_ID = #{r_id}

UPDATE EDUCATION
SET E_PLACE = #{e_place},
E_STARTED = #{e_started},
E_END = #{e_end},
E_NAME = #{e_name}
WHERE R_ID = #{r_id}

UPDATE SPECIAL_TECH
SET ST_AP = #{st_ap},
ST_LEVEL = #{st_level}
WHERE R_ID = #{r_id}

UPDATE FILE
SET F_ORI = #{f_ori},
F_SAVE = #{f_save},
F_SIZE = #{f_size}
WHERE R_ID = #{r_id}
